int nearestOpponentRight;

/*start functions*/
bool isRightCarDetectedNearby(){
    int i;
    for(i = 13; i < 24; i++){
        if(opponents[i] < 300){
            return true;
        }
    }
    return false;
}

bool isRightCarDetected(){
    int i;
    bool res = false;
    for(i = 13; i < 24; i++){
        if(opponents[i] < 600 && opponents[i] >= 300){
            res =  true;
        }

        if(opponents[i] < 300){
            return false;
        }
    }
    return res;
}

bool isRightCarDetectedFar(){
    int i;
    bool res = false;
    for(i = 13; i < 24; i++){
        if(opponents[i] <= 1000 && opponents[i] >= 600){
            res = true;
        }

        if(opponents[i] < 600){
            return false;
        }
    }
    return res;
}

bool isRightFree(){
    int i;
    for(i = 13; i < 24; i++){
        if(opponents[i] <= 1000 ){
            return false;
        }
    }
    return true;
}

int getNearestOpponentRightValue(){
    int i;
    nearestOpponentRight = 200;
    for(i = 13; i < 24; i++){
        if(opponents[i] < nearestOpponentRight){
            nearestOpponentRight = opponents[i];
        }
    }
    return nearestOpponentRight;
}

void rewardRightFree(){
    opponents_right_rwd = 0;
}

void rewardRightCarFar(){
    opponents_right_rwd = -10 + getNearestOpponentRightValue();
}

void rewardRightCar(){
    opponents_right_rwd = (-10 + getNearestOpponentRightValue())*2;
}

void rewardRightCarClose(){
    opponents_right_rwd = (-10 + getNearestOpponentRightValue())*4;
}
/*end functions*/

