/*start functions*/
// Track position guard functions
bool isCenter()
{
    return (-50 <= trackPos && trackPos <= 50);
}

bool isLimitRoad()
{   
    return ((-100 < trackPos && trackPos < -50) || (50 < trackPos && trackPos < 100));
}

// Return a positive value propostional to the error fo the trackPos from the Goal
int errorTrackPos()
{
    if (trackPos >= goalTrackPos)
    {
        return (trackPos - goalTrackPos);
    }
    else
    {
        return (goalTrackPos - trackPos);
    }
}


void rewardLimitRoad(){
    trackPos_rwd = 0 - errorTrackPos();
}

void rewardLeftOffRoad(){
    if(steer < 0){
        trackPos_rwd = -1 - errorTrackPos();
    }else{
        trackPos_rwd = -2 - errorTrackPos();
    }
}

void rewardRightOffRoad(){
    if(steer > 0){
        trackPos_rwd = -1 - errorTrackPos();
    }else{
        trackPos_rwd = -2 - errorTrackPos();
    }
}

void rewardCenter(){
    trackPos_rwd = 2 - errorTrackPos();
}
/*end functions*/

int stuckCounter = 0;

/*start functions*/
bool isStuck()
{
    return stuckCounter >= 10 && speedX < 1000;
}

bool isTurning()
{
    return (angle > 60 || angle < -60) && !isStuck();
    //return (track[9] < 10000) && angle < 20 && angle > -20 && !isLeftOffRoad() && !isRightOffRoad() && !isStuck();
}

bool isGoingStraight()
{
    return !isStuck() && !isTurning();
}

void updateStuckCounter(){
    if (speedX < 1000){
        stuckCounter = stuckCounter + 1;
    }
    else{
        stuckCounter = 0;
    }
}

int errorStraight(){
    if (goalSpeed >= speedX)
    {
        return goalSpeed - speedX;
    }
    else
    {
        return speedX - goalSpeed;
    }
}

int errorTurning(){
    if (goalSpeedTurning >= speedX)
    {
        return goalSpeedTurning - speedX;
    }
    else
    {
        return speedX - goalSpeedTurning;
    }
}

void rewardStuck()
{
    speedX_rwd = -2 ;
}

void rewardTurning()
{
    speedX_rwd = (-errorTurning() / 10) + 1;
}

void rewardStraight()
{
    speedX_rwd = (-errorStraight() / 10) + 1;
}
/*end functions*/

bool isDamaged(){
    return damage > damage_pre;
}

bool isNormal(){
    return !isDamaged();
}

void rewardDamaged(){
    damage_rwd = -40;
}

void rewardNormal(){
    damage_rwd = 0;
}
